@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix sosa: <http://www.w3.org/ns/sosa/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <file:///github/workspace/pop1999> ;
            sh:resultMessage "sosa:observedProperty is required" ;
            sh:resultPath sosa:observedProperty ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:message "sosa:observedProperty is required" ;
                    sh:select """
            PREFIX sh: <http://www.w3.org/ns/shacl#>
            PREFIX sosa: <http://www.w3.org/ns/sosa/>
            PREFIX geojson: <https://purl.org/geojson/vocab#>
            SELECT $this (sosa:observedProperty as ?path) (GROUP_CONCAT(?prop; separator=';') AS ?value)
            WHERE {
                OPTIONAL { $this sosa:observedProperty ?prop .
                 $this (sosa:hasResult|sosa:hasSimpleResult) ?result_so_observation }
                OPTIONAL { ?collection (sosa:hasMember|geojson:features)+ $this ; sosa:observedProperty ?prop }
            }
            GROUP BY ?prop
            HAVING (count(?prop) < 1)
        """ ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/sosa/#testObservedPropertyPresent> ;
            sh:value "" ],
        [ a sh:ValidationResult ;
            sh:focusNode <file:///github/workspace/c1> ;
            sh:resultMessage "Collection members must match sosa:observedProperty if present" ;
            sh:resultPath sosa:observedProperty ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:message "Collection members must match sosa:observedProperty if present" ;
                    sh:select """
            PREFIX sh: <http://www.w3.org/ns/shacl#>
            PREFIX sosa: <http://www.w3.org/ns/sosa/>
            PREFIX geojson: <https://purl.org/geojson/vocab#>
            SELECT $this (sosa:observedProperty as ?path) (GROUP_CONCAT(?prop; separator=';') AS ?value)
             WHERE {
                OPTIONAL { $this sosa:observedProperty ?prop }
                OPTIONAL { $this (sosa:hasMember|geojson:features)+ ?obs .
                    ?obs  sosa:observedProperty ?prop }
            }
            GROUP BY ?prop
            HAVING (count(?prop) < 1)
        """ ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/sosa/#testObservedPropertyConsistent> ;
            sh:value "" ] .

