    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ sosa:hasFeatureOfInterest [ ] ;
                    sosa:hasResult [ ] ;
                    sosa:madeBySensor "ea est culpa esse" ;
                    sosa:resultTime "2016-05-04T02:27:02.0Z" ] ;
            sh:resultMessage "Exactly 1 of sosa:observedProperty is required" ;
            sh:resultPath sosa:observedProperty ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:message "Exactly 1 of sosa:observedProperty is required" ;
                    sh:select """
            PREFIX sh: <http://www.w3.org/ns/shacl#>
            PREFIX sosa: <http://www.w3.org/ns/sosa/>
            PREFIX geojson: <https://purl.org/geojson/vocab#>
            SELECT $this (sosa:observedProperty as ?path) (GROUP_CONCAT(?prop; separator=';') AS ?value) WHERE {
                OPTIONAL { $this sosa:observedProperty ?prop }
                OPTIONAL { ?collection sosa:hasMember+|geojson:features+ $this ; sosa:observedProperty ?prop }
            }
            GROUP BY ?prop
            HAVING (count(?prop) != 1)
        """ ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/sosa/#testObservedProperty> ;
            sh:value "" ] .

